<div class="card__details details <?= $details ?>"><?php if('' != $card->category()): ?><div class="category"><?php echo $card->category(); ?></div><?php endif; ?><div class="title card__title"><?php echo htmlspecialchars($card->title(), ENT_QUOTES, 'UTF-8'); ?></div><?php if('' != $card->artist()):	 ?><div class="artist"><?php echo $card->artist(); ?></div><?php endif; ?><?php if('' != $card->blurb()):	 ?><div class="description card__description link-underline"><?php echo $card->blurb()->kt(); ?></div><?php endif; ?><?php if (isset($excerpt)): ?><div class="description card__description link-underline"><p><?php echo excerpt($card->copy()->kt(), $excerpt, 'words'); ?></p></div><?php elseif('' != $card->copy() && $card->type() != 'video'): ?><div class="description card__description link-underline"><?php echo $card->copy()->kt(); ?></div><?php endif; ?></div>