<div class="feature--full"><div class="row"><div class="feature__image-wrapper clearfix col-sm-<? if(isset($img_width)) echo $img_width ?>"><?php if($src = $feature->image($feature->img())): ?><img src="<?= $src->url() ?>"/><?php endif; ?></div><div class="feature__details details details--inset details--notop col-sm-<? if(isset($copy_width)) echo $copy_width ?>"><div class="title"><?php echo htmlspecialchars($feature->title(), ENT_QUOTES, 'UTF-8'); ?></div><div class="description feature__description link-underline"><?php echo $feature->blurb()->kt(); ?></div><?php if (isset($excerpt)): ?><div class="description feature__description link-underline"><p><?php echo excerpt($feature->description()->kt(), $excerpt, 'words'); ?></p></div><?php else: ?><div class="description feature__description link-underline"><?php echo $feature->description()->kt(); ?></div><?php endif; ?><?php snippet('more-link'); ?></div></div></div>