<div class="blog-most-popular sidebar-section"><h3 class="sidebar-section-title">Most Popular</h3><ol><?php $n=0; ?><?php foreach($pages->find('blog')->children()->visible()->filterBy('tags', 'popular', ',')->limit(5) as $entry): $n++; ?><li class="sidebar-item numbered-sidebar-item sidebar-highlight"><a href="<?= $entry->url() ?>" class="title"><?php echo $entry->title()->excerpt('8', 'words'); ?></a></li><?php endforeach; ?></ol></div>